import React, { useState } from 'react';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import './style.css'


export default function Popularhotel() {
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 2,

        responsive: [
            {
                breakpoint: 900,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 2,
                    initialSlide: 2,
                }
            }
        ]
    };

    ////////////////////////////////////////////////

    const [hotel , sethotel] = useState([]);

    
    return (

        <section className='hotelsec'>
            <div className='container'>

                <h1 className='mainh'>Most Popular Hotel</h1>

                <Slider {...settings}>
                    <article>

                        <figure>

                            <div className='imagwithloc'>
                                <img src='images/popular1.jpg' />
                                <p> <i className="fa-solid fa-location-dot"></i> Vietnam</p>
                            </div>

                            <div className='stars'>
                                <i className="fa-solid fa-star"></i>
                                <i className="fa-solid fa-star"></i>
                                <i className="fa-solid fa-star"></i>
                                <i className="fa-solid fa-star"></i>
                                <i className="fa-solid fa-star"></i>
                            </div>

                            <div className='hotelnfo'>
                                <h1>Fawlty Tower</h1>
                                <div></div>
                                <div></div>
                                <h1>$200.00 / <span>Per night</span></h1>
                            </div>
                        </figure>
                    </article>


                </Slider>

            </div>
        </section>
    )
}
