import React, { useEffect, useState } from 'react';
import './style.css';
import Headtitle from '../../common/headtitle/headtitle'
import axios from 'axios';

export default function Gallery() {

    const [gallery, setgallery] = useState([]);

    useEffect(() => {
        axios.get('js/data.json').then((res) => {
            setgallery(res.data.gallery)
        })
    }
        , []
    )

    const [singleimg , setimg] = useState([]);

    useEffect( ()=>
    {

    } )

    const openimage = (dta) => {
        // document.querySelector('.popupgall').style.display =  'block'
        return (

            <div className='popupgall'>
                <div className='poplayer'></div>
                <div className='popcontent'>
                    <i className="fa-solid fa-xmark"></i>
                    <img src={dta}></img>
                </div>
            </div>

        )
    }

    const finalgall = gallery.map((i) => {
        return (
            <figure key={i.id}>
                <div className='figdiv'>
                    <img src={i.image} />
                    <i onClick={openimage} className="fa-solid fa-image" ></i>
                </div>
                <h1>{i.title}</h1>
            </figure>

        )

    })
    return (
        <div>
            <Headtitle />

            <section className='gallsec'>

                <div className='container'>

                    <article>

                        {finalgall}

                    </article>



                </div>
            </section>
        </div>
    )
}
